import React, { useEffect, useState } from 'react';
import starWarsApi from '../services';

function Forms2() {
  const [planets, setPlanets] = useState([]);
  const [planetsFilter, setPlanetsFilter] = useState([]);
  const [name, setName] = useState('');

  const fetchAPI = async () => {
    setPlanets(await starWarsApi());
  };
  useEffect(() => {
    fetchAPI();
  }, []);



  useEffect(() => {
    if (name.length === 0) {
      setPlanetsFilter(planets);
    }
  }, [name.length, planets]);

  const filterName = ({ target: { value } }) => {
    setName(value);
    if (value.length > 0) {
      const nameFilter = planets
        .filter((e) => e.name.includes(value));
      setPlanetsFilter(nameFilter);
    }
  };

  return (
    <form>
      <table>
        <tr>
          <th>Name</th>
          <th>Rotation Period</th>
          <th>Orbital Period</th>
          <th>Diameter</th>
          <th>Climate</th>
          <th>Gravity</th>
          <th>Terrain</th>
          <th>Surface Water</th>
          <th>Population</th>
          <th>Films</th>
          <th>Created</th>
          <th>Edited</th>
          <th>URL</th>
        </tr>

        <div>
          <input type="text" onChange={ filterName } data-testid="name-filter" />
        </div>

        {planetsFilter.map((e) => (
          <tr key={ e.name }>
            <td>{e.name}</td>
            <td>{e.rotation_period}</td>
            <td>{e.orbital_period}</td>
            <td>{e.diameter}</td>
            <td>{e.climate}</td>
            <td>{e.gravity}</td>
            <td>{e.terrain}</td>
            <td>{e.surface_water}</td>
            <td>{e.population}</td>
            <td>{e.films.map((element) => `${element} `)}</td>
            <td>{e.created}</td>
            <td>{e.edited}</td>
            <td>{e.url}</td>
          </tr>
        ))}
      </table>
    </form>
  );
}

export default Forms2;
