import React, { useState, useContext } from 'react';
import StarWarContext from '../context/StarWarContext';

function Filters() {
  const [search, setFilter, setSearch] = useContext(StarWarContext);
  const [inputs, setInputs] = useState('');

  const handleChangeFilter = ({ target }) => {
    setInputs({ ...inputs, [target.name]: target.value });
    setFilter(setInputs);

  useEffect(() => {
    if (name.length === 0) {
      setPlanetsFilter(planets);
    }
  }, [name.length]);

  const filterName = ({ target: { value } }) => {
    setName(value);
    if (value.length > 0) {
      const nameFilter = planets
        .filter((e) => e.name.includes(value));
      setPlanetsFilter(nameFilter);
    }
  };

    if (target.name) {
      const filteredName = data.filter((planet) => (
        planet.name.toUpperCase().includes(target.value.toUpperCase())
      ));
      setSearch(filteredName);
    } else {
      setSearch(data);
    }
  };

  useEffect(() => {
    const nameFilter = search.filter((e) => e.name.toLowerCase(inputs)
      .includes(nameFilter));
    setSearch(setFilter);
  }, [search, setFilter, setSearch, inputs]);

  return (
    <div>
      <form>
        <label htmlFor="name">
          Filter Name:
          <input
            type="text"
            id="data-testid='name-filter'"
            name="nameFilter"
            // placeholder="NameFilter"
            value={ inputs }
            onChange={ handleChangeFilter }
          />
          <select>
            <option>name</option>
            <option>number</option>
          </select>

        </label>
      </form>
    </div>
  );
}
export default Filters;
