function StarWarProvider({ children }) {
  const [data, setData] = useState([]);
  const [filter, setFilter] = useState([]);
  const [search, setSearch] = useState([]);

  useEffect(() => {
    async function fetchAPI() {
      try {
        const response = await fetch('https://swapi.dev/api/planets');
        const dAta = await response.json();
        const retDataResults = dAta.results;

        const filterResult = retDataResults.map((planet) => {
          delete planet.residents;
          return planet;
        });
        setData(filterResult);
        setSearch(filterResult);
      } catch (error) {
        throw new Error(error.message);
      }
    }
    fetchAPI();
  }, []);

  const value = useMemo(() => ({
    data,
    filter,
    setFilter,
    search,
    setSearch,
  }), [data, filter, search, setSearch, setFilter]);

  return (
    <StarWarContext.Provider value={ value }>
      {children}
    </StarWarContext.Provider>
  );
}

StarWarProvider.propTypes = {
  children: PropTypes.node.isRequired,
};

export default StarWarProvider;
