import React, { useEffect, useMemo, useState } from 'react';
import reqApiFetch from '../service/requesteAPI';
import starWarContext from './StarWarContext';
import StarWarContext from './StarWarContext';

function StarWarProvider({ children }) {
  const [StarWarProvider, setStarWarProvider] = useState({ name: '', species: '',  })
  const [isLoading, setIsLoading] = useState(false);
  const fetchPlanets = async () => {
    try {
      setIsLoading(true);
      const data = await fetch('https://swapi.dev/api/planets');

      if (!data.ok) {
        const newError = await data.json();
        throw newError.message;
      }

      const json = await data.json();
      setGitData(json);
      history.push('/plantes');
    } catch (e) {
      setIsLoading({ name: '',
        species: '',
        gender: '',
        status: '',
        episode: '',
        error: e });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <StarWarContext.Provider value={ fetchPlanets }>
      {children}
    </StarWarContext.Provider>

