  const fetchPlanets = async () => {
    try {
      setIsLoading(true);
      const data = await fetch('https://swapi.dev/api/planets');

      if (!data.ok) {
        const newError = await data.json();
        throw newError.message;
      }
    } catch (error) {
      setIsLoading({ name: '', species: '', gender: '' });
