import React, { useContext } from 'react';
import StarWarContext from '../context/StarWarContext';
import fetchApi from '../service/requestAPI';
// import { requestAPIAxios, requestAPI } from '../service/requestAPI';
// const planet = e;
function Table() {
  const { search } = useContext(StarWarContext);
  const { inputs, setInputs } = useState('');
  const [setPlanet, planetFilter, setPlanetFilter] = useState([]);

  useEffect(() => {
    const nameFilter = search.filter((planet) => planet.name.toLowerCase(inputs)
      .includes(nameFilter));
    search(setFilter);
    setSearch(set)
  }, [search, inputs, setFilter]);

  useEffect(() => {
    fetchApi('https://swapi.dev/api/planets');
  }, []);

  const fetchPlanets = async () => {
    setPlanet(await fetchApi(fetchPlanets));
  };

  // const handleChange = ({ target }) => {
  //  setInputs(target.value);
  // };
  const handleChange = ({ target }) => {
    setInputs({ ...inputs, [target.name]: target.value });
    setFilter(setInputs);

    if (target.name) {
      const filteredName = search.filter((planet) => (
        planet.name.toUpperCase().includes(target.value.toUpperCase())
      ));
      setSearch(filteredName);
    } else {
      setSearch(inputs);
    }
  };

  return (
    <table>
      <thead>
        <tr>
          <th>Name</th>
          <th>Rotation Period</th>
          <th>Orbital Period</th>
          <th>Diameter</th>
          <th>Climate</th>
          <th>Gravity</th>
          <th>Terrain</th>
          <th>Surface Water</th>
          <th>Population</th>
          <th>Films</th>
          <th>Created</th>
          <th>Edited</th>
          <th>URL</th>
        </tr>
      </thead>
      <tbody>
        <div>
          <input
            type="text"
            id="data-testid='name-filter'"
            name="nameFilter"
            placeholder="digite o nome"
            value={ nameFilter }
            onChange={ handleChange }
          />
        </div>
        {
          search
            .map((planet) => (
              <tr key={ planet.name }>
                <td>{ planet.name }</td>
                <td>{ planet.rotation_period }</td>
                <td>{ planet.orbital_period }</td>
                <td>{ planet.diameter }</td>
                <td>{ planet.climate }</td>
                <td>{ planet.gravity }</td>
                <td>{ planet.terrain }</td>
                <td>{ planet.surface_water }</td>
                <td>{ planet.population }</td>
                <td>
                  { planet
                    .map((film, i) => <p key={ `${film}-${i}` }>{film}</p>)}
                </td>
                <td>{ planet.created }</td>
                <td>{ planet.edited}</td>
                <td>{ planet.url }</td>
              </tr>
            ))
        }
      </tbody>
    </table>
  );
}

export default Table;

