import React, { useContext } from 'react';
import StarWarContext from '../context/StarWarContext';
// import { requestAPIAxios, requestAPI } from '../service/requestAPI';

function Table() {
  const { search, data } = useContext(StarWarContext);
  const [inputs, setInputs] = useState('');
  const nameFilter = search.filter((planet) => planet.name.toLowerCase(inputs)
    .includes(nameFilter));

  // const [setFilter, setSearch] = useContext();

  const handleChangeFilter = ({ target }) => {
    setInputs(target.value);
  };

  // useEffect(() => {
  //  setSearch(setFilter);
  // }, [search, setFilter, setSearch]);

  return (
    <table>
      <thead>
        <tr>
          <th>Name</th>
          <th>Rotation Period</th>
          <th>Orbital Period</th>
          <th>Diameter</th>
          <th>Climate</th>
          <th>Gravity</th>
          <th>Terrain</th>
          <th>Surface Water</th>
          <th>Population</th>
          <th>Films</th>
          <th>Created</th>
          <th>Edited</th>
          <th>URL</th>
        </tr>
      </thead>
      <tbody>

        <div>
          <input
            type="text"
            id="data-testid='name-filter'"
            name="nameFilter"
            placeholder="digite o noooome que procura"
            value={ inputs }
            onChange={ handleChangeFilter }
          />
        </div>

        {
          search
            .filter((planet) => planet.name.toLowerCase().includes(inputs))
            .map((planet) => (
              <tr key={ planet.name }>
                <td>{ planet.name }</td>
                <td>{ planet.rotation_period }</td>
                <td>{ planet.orbital_period }</td>
                <td>{ pplanetlanet.diameter }</td>
                <td>{ e.climate }</td>
                <td>{ e.gravity }</td>
                <td>{ e.terrain }</td>
                <td>{ e.surface_water }</td>
                <td>{ e.population }</td>
                <td>
                  {e.films
                    .map((film, i) => <p key={ `${film}-${i}` }>{film}</p>)}
                </td>
                <td>{ e.created }</td>
              </tr>
            ))
        }
      </tbody>
    </table>
  );
}

export default Table;
