import React, { useContext } from 'react';
import StarWarContext from '../context/StarWarContext';
// import { requestAPIAxios, requestAPI } from '../service/requestAPI';

function Table() {
  const { search } = useContext(StarWarContext);
  const [setFilter, setSearch] = useContext();
  const [inputs, setInputs] = useState({ tituloFiltrado: '' });

  const handleChangeFilter = ({ target }) => {
    setInputs({ ...inputs, [target.name]: target.value });
  };

  useEffect(() => {
    const filterName = search.filter((el) => el.inputs.toLowerCase()
      .incluides(tituloFiltrado));
    setSearch(setFilter);
  }, [search, setFilter, setSearch]);

  return (
    <table>
      <thead>
        <tr>
          <th>Name</th>
          <th>Rotation Period</th>
          <th>Orbital Period</th>
          <th>Diameter</th>
          <th>Climate</th>
          <th>Gravity</th>
          <th>Terrain</th>
          <th>Surface Water</th>
          <th>Population</th>
          <th>Films</th>
          <th>Created</th>
          <th>Edited</th>
          <th>URL</th>
        </tr>
      </thead>
      <tbody>
        {
          search
            .filter((el) => el.inputs.toLower().el.toUpperCase())
            .map((e) => (
              <tr key={ e.name }>
                <td>{ e.name }</td>
                <td>{ e.rotation_period }</td>
                <td>{ e.orbital_period }</td>
                <td>{ e.diameter }</td>
                <td>{ e.climate }</td>
                <td>{ e.gravity }</td>
                <td>{ e.terrain }</td>
                <td>{ e.surface_water }</td>
                <td>{ e.population }</td>
                <td>
                  {e.films
                    .map((film, i) => <p key={ `${film}-${i}` }>{film}</p>)}
                </td>
                <td>{ e.created }</td>
              </tr>
            ))
        }
      </tbody>
    </table>
  );
}

export default Table;
