import React, { useContext, useEffect } from 'react';
import StarWarProvider from '../context/StarWarProvider';

function Forms() {
  const {
    dataPlanets,
    setDataPlanets,
    search,
    setSearch,
    setListPlanet,
    listPlanet } = useContext(StarWarProvider);

  useEffect(() => {
    setDataPlanets(data);
    if (search.length > 0) {
      const dataSearch = dataPlanets.filter((el) => el.name.includes(search));
      setListPlanet(dataSearch);
    } else {
      setListPlanet(dataPlanets);
    }
  }, [data, dataPlanets, search]);

  return (
    <div>
      <input
        type="text"
        data-testid="name-filter"
        name="search"
        placeholder="digite o nome do planeta"
        value={ search }
        onChange={ ({ target }) => setSearch(target.value) }
      />
    </div>
  );
}
export default Forms;
