import React, { useState, useContext } from 'react';
import StarWarContext from '../context/StarWarContext';

function Filters() {
  const [search, setFilter, setSearch] = useContext(StarWarContext);
  const [inputs, setInputs] = useState({ nameFilter: '' });

  const handleChangeFilter = ({ target }) => {
    setInputs({ ...inputs, [target.name]: target.value });
  };

  useEffect(() => {
    const nameFilter = inputs.filter((el) => el.inputs.toLowerCase()
      .incluides(nameFilter));
    setSearch(setFilter);
  }, [search, setFilter, setSearch]);

  return (
    <div>
      <form>
        <label htmlFor="name">
          Filter Name:
          <input
            type="text"
            id="data-testid='name-filter"
            name="nameFilter"
            // placeholder="Nome"
            value={ inputs.nameFilter }
            onChange={ handleChangeFilter }
          />
          <select>
