import React, { useState, useContext } from 'react';
import StarWarContext from '../context/StarWarContext';

function Filters() {
  const [search, setFilter, setSearch] = useContext(StarWarContext);
  const [inputs, setInputs] = useState('');

  const handleChangeFilter = ({ target }) => {
    setInputs({ ...inputs, [target.name]: target.value });
  };

  useEffect(() => {
    const nameFilter = search.filter((e) => e.name.toLowerCase(inputs)
      .includes(nameFilter));
    setSearch(setFilter);
  }, [search, setFilter, setSearch]);

  return (
    <div>
      <form>
        <label htmlFor="name">
          Filter Name:
          <input
            type="text"
            id="data-testid='name-filter'"
            name="nameFilter"
            // placeholder="Name"
            value={ inputs.nameFilter }
            onChange={ handleChangeFilter }
          />
          <select>
            <option>name</option>
            <option>number</option>
          </select>

        </label>
      </form>
    </div>
  );
}
export default Filters;
