const NumberFilter = (listPlanet, filterInp) => {
  const list = listPlanet;
  filterNumber.forEach((element) => {
    const planetList = list.filter((el) => {
      if (element.comparison === 'maior que') {
        return +el[element.column] > +el.value;
      }
      if (element.comparison === 'menor que') {
        return +el[element.column] < +el.value;
      }
      if (element.comparison === 'igual a') {
        return +el[element.column] === +el.value;
      }
      return false;
    });
    filterNumber = planetList;
  });
  return filterNumber;
};
export default NumberFilter;
