import React, { useContext, useEffect } from 'react';
import StarWarProvider from '../context/StarWarProvider';

function Forms() {
  const { data, dataPlanet,
     setDataPlanet, search,
      setSearch, setListPlanet, listPlanet } = useContext(StarWarProvider);

  useEffect(() => {
    setDataPlanet(data);
    if (search.length > 0) {
      const dataSearch = dataPlanet.filter((el) => el.name.includes(search));
      setListPlanet(dataSearch);
    } else {
      setListPlanet(dataPlanet);
    }
  }, [data, dataPlanet, search]);

  return (
    <div>
      <input
        type="text"
        data-testid="name-filter"
        name="search"
        placeholder="digite o nome do planeta"
        value={ search }
        onChange={ ({ target }) => setSearch(target.value) }
      />
    </div>
  );
}
export default Forms;
