import React, { useEffect, useMemo, useState } from 'react';
import reqApiFetch from '../service/requesteAPI';
import starWarContext from './StarWarContext';

function starWarProvider({ children }) {
  const [data, setData] = useState([]);
  useEffect(() => {
    reqApiFetch().then((result) => setData(result));
  }, []);

  const value = useMemo(() => ({
    data,
  }), [data]);

  return (
    <SstarWarContext.Provider value={ value }>
      { children }
    </SstarWarContext.Provider>
  );
}

export default starWarProvider;
