import React, { useState } from "react";


export default function Filme({movie}) {
  const [closeButton, setCloseButton] = useState();

  useEffect(() => {
    setCloseButton(movie)
  });
  console.log(movie);
  return (
    <div>
  {
    !movie ? null : (
      <div className="modal-background">
    <div className="modal-container">
          <h2 className="modal-h2">{movie.titulo}</h2>
          <h2 className="modal-h2">{movie.titulo2}</h2>

          <div className="modal-imgBox">
          <img src={movie.capaDoFilme2} alt="capa do filme" className="modal-img" />
          {movie.trailer}
          </div>

          <div className="modal-sinospe">
          <h3 className="modal-h3">Sinopse:</h3>
          <p className="modal-p">{movie.sinopse}</p>
          </div>
    </div>
    </div> 
    )
  }
  </div>
  );
}
