import React, { useContext } from 'react';
import { AppContext } from '../context/AppProvider';
import StarWarContext from '../context/StarWarContext';
// import { requestAPIAxios, requestAPI } from '../service/requestAPI';

function Table() {
  const { search } = useContext(StarWarContext);
  const [query, setQuery] = useState('');
  // const [setFilter, setSearch] = useContext();

  // const handleChangeFilter = ({ target }) => {
  //  setInputs(target.value);
  // };

  // useEffect(() => {
  //  setSearch(setFilter);
  // }, [search, setFilter, setSearch]);

  return (
    <table>
      <thead>
        <tr>
          <th>Name</th>
          <th>Rotation Period</th>
          <th>Orbital Period</th>
          <th>Diameter</th>
          <th>Climate</th>
          <th>Gravity</th>
          <th>Terrain</th>
          <th>Surface Water</th>
          <th>Population</th>
          <th>Films</th>
          <th>Created</th>
          <th>Edited</th>
          <th>URL</th>
        </tr>
      </thead>
      <tbody>
        <tbody>
          {results
          && results.map((planet, index) => <Planet key={ index } info={ planet } />)}
        </tbody>
        <div>
          <input
            type="text"
            placeholder="Search"
            onChange={ (e) => {
              setQuery(e.target.value);
            } }
            value={ query }
            data-testid="name-filter"
          />

        </div>
        {
          search
            .filter((e) => e.name.toLowerCase().includes(input))
            .map((e) => (
              <tr key={ e.name }>
                <td>{ e.name }</td>
                <td>{ e.rotation_period }</td>
                <td>{ e.orbital_period }</td>
                <td>{ e.diameter }</td>
                <td>{ e.climate }</td>
                <td>{ e.gravity }</td>
                <td>{ e.terrain }</td>
                <td>{ e.surface_water }</td>
                <td>{ e.population }</td>
                <td>
                  {e.films
                    .map((film, i) => <p key={ `${film}-${i}` }>{film}</p>)}
                </td>
                <td>{ e.created }</td>
              </tr>
            ))
        }
      </tbody>
    </table>
  );
}

export default Table;
