    name: '',
    gender: 'Todos',
    episode: 0,
  });

  const value = useMemo(() => ({
    data,
    filters,
    setFilters,
  }), [data, filters, setFilters]);

  const handleChange = ({ target }) => {
    setInputs({ ...inputs, [target.name]: target.value });
  };

  const handleClick = () => {
    setFilters(inputs);
  };
  useEffect(() => {
    const nameFilter = search.filter((planet) => planet.name.toLowerCase(inputs)
      .includes(nameFilter));
    setSearch(setFilter);
  }, [search, setFilter, setSearch, inputs]);

  return (

    <form>

      <input
        type="text"
        name="name"
        id="name"
        // placeholder="name"
        value={ inputs }
        onChange={ handleChange }
      />

      <select name="gender" id="gender">
        <option value="">Todos</option>
        <option value="Female">Feminino</option>
        <option value="Male">Masculino</option>
        onChange=
        { handleChange }
        value=
        { inputs }
      </select>

      <select name="episode" id="episode" onClick={ handleClick }>
        <option value="maior que">maior que</option>
        <option value="menor que">menor que</option>
        <option value="igual a">igual a</option>
      </select>

      <input
