function Fomrs() {
  const { input, setInput } = useContext(StarWarProvider);
  const { filters, setFilters } = useContext(StarWarContext);
  // const [data, setData] = useState([]);
  // const [filters, setFilters] = useState({});
  useEffect(() => {
    reqApiFetch().then((response) => setData(response));
  }, []);

  const value = useMemo(() => ({
    data,
    filters,
    setFilters,
  }), [data, filters, setFilters]);

  const handleChange = ({ target }) => {
    setInput(target.value);
  };

  const handleClick = () => {
    setFilters(input);
  };

